
public class Forloop {

	public static void main(String[] args) {
		//nested loops works -
		int k=3;
		for(int i=3;i<5;i++)  //(outer for loop) this block will loop for 4 times
		{
		//System.out.println( "outer loop started");
		for(int j=1;j<5;j++) // inner loop
		{
		System.out.print( k);
		System.out.print("\t");
		k++;
		}
		System.out.println( "");
		}
		}
		}

